import React from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink, Button, Row, Col } from 'reactstrap';
import classnames from 'classnames';
import Users from './Users.js';
import web3 from './web3.js';
import ipfs from './ipfs.js';
import storehash from './storehash.js';

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = {
      activeTab: '1',
      ipfsHash: null,
      buffer: '',
      ethAddress: '',
      blockNumber: '',
      transactionHash: '',
      gasUsed: '',
      txReceipt: ''
    };
    this.userLogIn = this.userLogIn.bind(this)
    this.employerLogIn = this.employerLogIn.bind(this)
    this.verifierLogIn = this.verifierLogIn.bind(this)
  }

  userLogIn() {

  }

  employerLogIn() {

  }

  verifierLogIn() {

  }

  toggle(tab) {
    if (this.state.activeTab !== tab) {
      this.setState({
        activeTab: tab
      });
    }
  }

  render() {
    return (
      <div>
        <img src={require('./header.png')} width="100%"/>
        <Nav tabs>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '1' })}
              onClick={() => { this.toggle('1'); }}
            >
              Users
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '2' })}
              onClick={() => { this.toggle('2'); }}
            >
              Verifiers
            </NavLink>
          </NavItem>
          <NavItem>
            <NavLink
              className={classnames({ active: this.state.activeTab === '3' })}
              onClick={() => { this.toggle('3'); }}
            >
              Employers
            </NavLink>
          </NavItem>
        </Nav>
        <TabContent activeTab={this.state.activeTab}>
          <TabPane tabId="1">
            <Row>
              <Col sm="12">
                <Button outline size="sm" block onClick={this.userLogIn}>Sign In</Button>
                <Users></Users>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="2">
            <Row>
              <Col sm="12">
                <Button outline size="sm" block onClick={this.verifierLogIn}>Sign In</Button>
              </Col>
            </Row>
          </TabPane>
          <TabPane tabId="3">
            <Row>
              <Col sm="12">
                <Button outline size="sm" block onClick={this.employerLogIn}>Sign In</Button>
              </Col>
            </Row>
          </TabPane>
        </TabContent>
      </div>
    );
  }
}
