'use strict';

var promisify = require('promisify-es6');

var cleanMultihash = require('../utils/clean-multihash');

module.exports = function (send) {
  var objectGet = require('./get')(send);

  return promisify(function (multihash, dLink, opts, callback) {
    if (typeof opts === 'function') {
      callback = opts;
      opts = {};
    }

    if (!opts) {
      opts = {};
    }

    try {
      multihash = cleanMultihash(multihash, opts);
    } catch (err) {
      return callback(err);
    }

    send({
      path: 'object/patch/rm-link',
      args: [multihash, dLink.name]
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      objectGet(result.Hash, {
        enc: 'base58'
      }, callback);
    });
  });
};
