'use strict';

var promisify = require('promisify-es6');

var dagPB = require('ipld-dag-pb');

var DAGLink = dagPB.DAGLink;

var cleanMultihash = require('../utils/clean-multihash');

var bs58 = require('bs58');

var LRU = require('lru-cache');

var lruOptions = {
  max: 128
};
var cache = LRU(lruOptions);

module.exports = function (send) {
  return promisify(function (multihash, options, callback) {
    if (typeof options === 'function') {
      callback = options;
      options = {};
    }

    if (!options) {
      options = {};
    }

    try {
      multihash = cleanMultihash(multihash, options);
    } catch (err) {
      return callback(err);
    }

    var node = cache.get(multihash);

    if (node) {
      return callback(null, node.links);
    }

    send({
      path: 'object/links',
      args: multihash
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      var links = [];

      if (result.Links) {
        links = result.Links.map(function (l) {
          return new DAGLink(l.Name, l.Size, Buffer.from(bs58.decode(l.Hash)));
        });
      }

      callback(null, links);
    });
  });
};
