'use strict';

var promisify = require('promisify-es6');

var once = require('once');

var cleanMultihash = require('../utils/clean-multihash');

var SendOneFile = require('../utils/send-one-file');

module.exports = function (send) {
  var objectGet = require('./get')(send);

  var sendOneFile = SendOneFile(send, 'object/patch/append-data');
  return promisify(function (multihash, data, opts, _callback) {
    if (typeof opts === 'function') {
      _callback = opts;
      opts = {};
    }

    var callback = once(_callback);

    if (!opts) {
      opts = {};
    }

    try {
      multihash = cleanMultihash(multihash, opts);
    } catch (err) {
      return callback(err);
    }

    sendOneFile(data, {
      args: [multihash]
    }, function (err, result) {
      if (err) {
        return callback(err);
      }

      objectGet(result.Hash, {
        enc: 'base58'
      }, callback);
    });
  });
};
