'use strict';

var promisify = require('promisify-es6');

var Block = require('ipfs-block');

var CID = require('cids');

var once = require('once');

var SendOneFile = require('../utils/send-one-file');

module.exports = function (send) {
  var sendOneFile = SendOneFile(send, 'block/put');
  return promisify(function (block, cid, _callback) {
    // TODO this needs to be adjusted with the new go-ipfs http-api
    if (typeof cid === 'function') {
      _callback = cid;
      cid = {};
    }

    var callback = once(_callback);

    if (Array.isArray(block)) {
      return callback(new Error('block.put accepts only one block'));
    }

    if (typeof block === 'object' && block.data) {
      block = block.data;
    }

    sendOneFile(block, {}, function (err, result) {
      if (err) {
        return callback(err); // early
      }

      callback(null, new Block(block, new CID(result.Key)));
    });
  });
};
